class Solution {
private:
    void dfs(int r, int c, vector<vector<bool>> &vis, vector<vector<int>> &g, vector<vector<int>> &ans, int &OR_COL, int &newColor){
        int n=g.size(), m=g[0].size();
        vis[r][c]=1;

        int delR[4] = {0,0,-1,1};
        int delC[4] = {1,-1,0,0};

        for(int i=0; i<4; i++){
            int R = r+delR[i];
            int C = c+delC[i];

            if(R<n&&R>=0&&C<m&&C>=0){
                if((R==0||C==m-1||R==n-1||C==0) && g[R][C]==OR_COL){
                    ans[R][C]=newColor;
                }

                if(!vis[R][C] && g[R][C]==OR_COL) {
                    dfs(R,C,vis,g,ans,OR_COL,newColor);
                }
            }
        }
    }    

    void colorRemaining(int r, int c, vector<vector<bool>> &vis, vector<vector<int>>& g, vector<vector<int>>& ans, int &newColor){
        int n=g.size(), m=g[0].size();

        int delR[4] = {0,0,-1,1};
        int delC[4] = {1,-1,0,0};        

        for(int i=0; i<4; i++){
            int R = r+delR[i];
            int C = c+delC[i];

            if(R<n&&R>=0&&C<m&&C>=0 && vis[R][C]){
                ans[R][C]=newColor;
            }            
        }        
    }
public:
    vector<vector<int>> colorBorder(vector<vector<int>>& g, int row, int col, int color) {
        int n=g.size(), m=g[0].size();
        int originalColor = g[row][col];
        vector<vector<int>> ans = g;
        vector<vector<bool>> vis(n,vector<bool>(m,0));

        dfs(row,col,vis,g,ans,originalColor,color);

        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(!vis[i][j]) colorRemaining(i,j,vis,g,ans,color);
            }
        }

        return ans;
    }
};
