class Solution {
public:
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        int m = grid.size();
        int n = grid[0].size();

        queue<pair<int,int>> q;
        q.push({row, col});
        int iniCol = grid[row][col];

        vector<int> dx = {0 , 1 , 0 , -1};
        vector<int> dy = {-1 , 0 , 1 , 0};
        vector<vector<int>> vis(m , vector<int> (n , 0));
        vis[row][col] = 1;

        while (!q.empty()){
            int r = q.front().first;
            int c = q.front().second;
            q.pop();
            bool temp = false;
            if (r==m-1 || r==0 || c==n-1 || c==0)
                temp = true;

            bool flag = false;
            for (int i=0 ; i<4 ; i++){
                int nr = r + dx[i];
                int nc = c + dy[i];
                if (nr>=0 && nr<m && nc>=0 && nc<n && !vis[nr][nc]){
                    if (grid[nr][nc]==iniCol){
                        vis[nr][nc] = 1;
                        q.push({nr,nc});
                    }
                    else{
                        flag = true;
                    }
                }
            }
            if (flag || temp)
                grid[r][c] = color;
        }
        return grid;    
    }
};
