class Solution {
public:
bool is_safe(int i,int j,int row,int col){
    if(i<0 ||j<0 || i>=row || j>=col)return false;
    return true;
}
int deli[4]={0,1,0,-1};
int delj[4]={1,0,-1,0};
void dfs(int i,int j,int row,int col,int curcol,int newcol,vector<vector<int>>& grid,vector<vector<int>>& vis,vector<vector<int>>& ggrid){
    int temp=0;
    for(int k=0;k<4;k++){
        int new_i=i+deli[k];
        int new_j=j+delj[k];
        if(is_safe(new_i,new_j,row,col)&&grid[new_i][new_j]==curcol)temp++;
    }
    if(temp!=4)ggrid[i][j]=newcol;
    for(int k=0;k<4;k++){
        int new_i=i+deli[k];
        int new_j=j+delj[k];
        if(is_safe(new_i,new_j,row,col)&&grid[new_i][new_j]==curcol && !vis[new_i][new_j]){
            vis[new_i][new_j]=1;
            dfs(new_i,new_j,row,col,curcol,newcol,grid,vis,ggrid);
        }
    }

}
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int i, int j, int newcol) {
        int row=grid.size();
        int col=grid[0].size();
        int curcol=grid[i][j];
        if (curcol==newcol)return grid;
        vector<vector<int>>vis(row,vector<int>(col,0));
        vector<vector<int>>ggrid(row,vector<int>(col,0));
        for(int i=0;i<row;i++){
            for(int j=0;j<col;j++){
                ggrid[i][j]=grid[i][j];
            }
        }
        vis[i][j]=1;
        dfs(i,j,row,col,curcol,newcol,grid,vis,ggrid);
        return ggrid;
    }
};



