class Solution {
public:

int row[4] = {1, -1, 0, 0};
int col[4] = {0, 0, 1, -1};

void dfs(vector<vector<int>>& grid, vector<vector<int>>& visited, int i, int j, int &target, int& color){
    if(i<0 || i>=grid.size() || j<0 || j>=grid[0].size() || visited[i][j]==1 || grid[i][j]!=target)
    return;

    visited[i][j] = 1;
    bool flag = 0;

    for(int p = 0 ;p<4 ;p++){
        int newX = i+row[p];
        int newY = j+col[p];

        if(i==0 || i==grid.size()-1 || j==0 || j==grid[0].size()-1 || (visited[newX][newY] == 0 && grid[i][j]!=grid[newX][newY]) )
        flag = 1;

        dfs(grid, visited, newX, newY, target, color);
    }

    if(flag)
    grid[i][j] = color;  
}

    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {

        int target = grid[row][col];
        vector<vector<int>> visited(grid.size(), vector<int>(grid[0].size(), 0));

        dfs(grid, visited, row, col, target, color);
        return grid;
    }
};
```

