    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        int n = grid.size();
        int m = grid[0].size();
        vector<vector<int>>vis(n,vector<int>(m,0));
        //at first visit all the nodes which have the color = oldcolor and connected to current component 
        queue<pair<int,int>>q;
        vector<pair<int,int>>v;
        q.push({row,col});
        int oldcolor = grid[row][col];
        int delrow[4] = {0,-1,0,+1};
        int delcol[4] = {+1,0,-1,0};
        vis[row][col] = 1;
        //after getting all the nodes of the current component please check which of those nodes lie in the circle 
        while(q.empty() == false){
            int row = q.front().first ;
            int col = q.front().second;
            v.push_back(make_pair(row,col));
            q.pop();
            for(int i=0;i<4;i++){
                int nrow = row + delrow[i];
                int ncol = col + delcol[i];
                if(nrow>=0 && ncol>=0 && nrow<n && ncol<m 
                && !vis[nrow][ncol] && grid[nrow][ncol] == oldcolor){
                    vis[nrow][ncol] = 1;
                    q.push({nrow,ncol});
                }
            }
        }
        //now inside v i have got all the nodes which are connected to the current compnent 
        //and all those nodes have the color oldcolor

        //now i have to check all the nodes in the current component which are boundary nodes 


        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                vis[i][j] = 0;
            }
        }
        for(int i=0;i<v.size();i++){
            q.push({v[i].first,v[i].second});
            cout<<v[i].first<<" "<<v[i].second<<endl;
            vis[v[i].first][v[i].second] = 1;
        }
        cout<<v.size()<<endl;
        vector<vector<int>> arr = grid;
        while(q.empty() == false){
            int row = q.front().first;
            int col = q.front().second;
            if(row == 0 || col == 0 || row == n-1 || col == m-1){
                arr[row][col] = color;
                q.pop();
                continue;
            }
            q.pop();
            for(int i=0;i<4;i++){
                int nrow = row + delrow[i];
                int ncol = col + delcol[i];
                if(nrow>=0 && ncol>=0 && nrow<n && ncol<m){
                    if(grid[nrow][ncol]!=oldcolor){
                        arr[row][col] = color;
                    }
                }
            }
        }
        return arr;
    }
};
