```C++ []
class Solution {
public:
    int dx[4] = {0,1,0,-1};
    int dy[4] = {1,0,-1,0};
    void dfs(vector<vector<int>>& grid, int x, int y, int num){
        grid[x][y]= -num;
        int cnt=0;
        for(int i=0; i<4; ++i){
            int x1 = x+dx[i], y1=y+dy[i];
            if(x1<0 || x1>=grid.size() || y1<0 || y1>=grid[0].size() || abs(grid[x1][y1]) != num) continue;
            cnt+=1;
            if(grid[x1][y1] != -num)dfs(grid,x1,y1,num);
        }
        if(cnt==4) grid[x][y] = num;
    }
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        int num = grid[row][col];
        dfs(grid,row,col,num);
        for(int i=0; i<grid.size(); ++i){
            for(int j=0; j<grid[0].size(); ++j){
                if(grid[i][j] == -num) grid[i][j]=color;
            }
        }
        return grid;
    }
};
```

```Python3 []
class Solution:
    def colorBorder(self, grid: List[List[int]], row: int, col: int, color: int) -> List[List[int]]:
        M, N = len(grid), len(grid[0])
        q = collections.deque()
        q.append((row, col))
        dir_lst = [(1, 0), (-1, 0), (0, 1), (0, -1)]
        seen = set()
        seen.add((row, col))
        border = set()
        while q:
            i, j = q.popleft()
            is_border = i in [0, M-1] or j in [0, N-1]
            for di, dj in dir_lst:
                ni, nj = i+di, j+dj
                if 0<=ni<M and 0<=nj<N and (ni, nj) not in seen:
                    if grid[ni][nj]!=grid[i][j]:
                        is_border = True
                    else:
                        seen.add((ni, nj))
                        q.append((ni, nj))
            if is_border:
                border.add((i, j))

        for i, j in border:
            grid[i][j] = color
        return grid
```

```Java []
class Solution {
    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        int n = grid.length;
        int m = grid[0].length;

        if (grid[row][col]==color)
            return grid;

        boolean[][] visited = new boolean[n][m];
        dfs(grid,row,col,grid[row][col],visited,color,n,m);

        return grid;
    }
    void dfs(int[][] grid,int i,int j,int oldColor,boolean[][] visited,int targetColor,int n,int m) {
        if (i>=n || i<0 || j<0 || j>=m || grid[i][j]!=oldColor || visited[i][j])
            return;

        visited[i][j] = true;
        boolean border = false;

        if (i==0 || j==0 || j==m-1 || i==n-1 || grid[i+1][j]!=oldColor || grid[i-1][j]!=oldColor || grid[i][j-1]!=oldColor || grid[i][j+1]!=oldColor)
            border = true;

        dfs(grid,i+1,j,oldColor,visited,targetColor,n,m);
        dfs(grid,i-1,j,oldColor,visited,targetColor,n,m);
        dfs(grid,i,j+1,oldColor,visited,targetColor,n,m);
        dfs(grid,i,j-1,oldColor,visited,targetColor,n,m);

        if (border)
            grid[i][j] = targetColor;
    }
}
```

