        if(grid[i-1][j]==color) cnt++;
        if(grid[i+1][j]==color) cnt++;
        if(grid[i][j-1]==color) cnt++;
        if(grid[i][j+1]==color) cnt++;
        return cnt<4;
    }
    public void dfs(int[][] grid,int[][] visited,int row,int col,int color,int oldcolor,int[][] board,boolean[][] safe){
        visited[row][col]=1;
        if(safe[row][col]==true){
        board[row][col] = color;
        }
        int m = grid.length;
        int n = grid[0].length;
        int[] drow = {-1,0,1,0};
        int[] dcol = {0,1,0,-1};   
           for(int i=0;i<4;i++){
               int newr = row + drow[i];
               int newc = col + dcol[i];
if(newr>=0 && newr<m && newc>=0 && newc<n && visited[newr][newc]!=1 && grid[newr][newc]==oldcolor){
                   dfs(grid,visited,newr,newc,color,oldcolor,board,safe);
