        /*all adjacent cell which same value as grid[row][col] will be color*/
     /*we have used BFS traversal first we are inseting the row col and after removing we are seeing if its adjacent cell has same value then we insert that adjacent cell into the queue and another array we have used so if count <4 so we color that particular res[x][y]=color*/ 
     /*Also we have used an 2d boolean array of same length as grid so that we marked that cell which is already visited and not to visit again*/
        int m=grid.length;
        int n=grid[0].length;
        int res[][]=new int[m][n];
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                res[i][j]=grid[i][j];
            }
        }
        Queue<int[]> q=new LinkedList<>();
        boolean visited[][]=new boolean[m][n];
        int t[]=new int[2];
        int v=grid[row][col];
        if(v==color)return grid;
        t[0]=row;
        t[1]=col;
        q.add(t);
       while(!q.isEmpty()){
           int size=q.size();
           for(int i=0;i<size;i++){
               int arr[]=q.remove();
               int x=arr[0];
               int y=arr[1];
               if(visited[x][y])continue;
               visited[x][y]=true;
               int c=y+1;
               int c1=y-1;
               int r=x+1;
               int r1=x-1;
               int count=0;
               if(c<n && grid[x][c]==v){
                   count++;
                   int temp[]=new int[2];
                   temp[0]=x;
                   temp[1]=c;
                   q.add(temp);
