                if(r1>=0 && grid[r1][y]==v){
                   count++;
                   int temp[]=new int[2];
                   temp[0]=r1;
                   temp[1]=y;
                   q.add(temp);
               }
               System.out.print(count+"  ");
               if(count!=4){
                   res[x][y]=color;
               }
           }
       }
        return res;
    }
}
