# Code
```
class Solution:
    def colorBorder(self, grid: List[List[int]], row: int, col: int, color: int) -> List[List[int]]:
        m, n = len(grid), len(grid[0])
        def out_of_boundary(x, y):
            return x < 0 or y < 0 or x >= m or y >= n

        def get_color(x, y):
            if out_of_boundary(x, y):
                return -2
            return grid[x][y]

        def check_inside(x, y, target):
            dirs = [1, 0, -1, 0]
            for i in range(4):
                color = get_color(x + dirs[i], y + dirs[(i + 1) % 4])
                if color != -1 and color != target:
                    return False
            return True

        p = []

        def dfs(x, y, target):
            if out_of_boundary(x, y):
                return
            if grid[x][y] != target:
                return
            if check_inside(x, y, target):
                p.append((x, y, grid[x][y]))
            grid[x][y] = -1
            dfs(x + 1, y, target)
            dfs(x - 1, y, target)
            dfs(x, y + 1, target)
            dfs(x, y - 1, target)

        dfs(row, col, grid[row][col])
        for i in range(m):
            for j in range(n):
                if grid[i][j] == -1:
                    grid[i][j] = color

        for x, y, color in p:
            grid[x][y] = color

        return grid

