class Solution:
    def colorBorder(self, grid: List[List[int]], row: int, col: int, color: int) -> List[List[int]]:
        m = len(grid)
        if m == 0:
            return grid
        n = len(grid[0])

        dr = [[-1,0],[0,1],[1,0],[0,-1]]
        visited = set()
        painted = set()
        cl = grid[row][col]

        q = deque()
        q.append([row, col])
        while q:
            for i in range(len(q)):
                paint = False
                y, x = q.popleft()
                key = str(y) + ':' + str(x)
                if key in visited:
                    continue
                visited.add(key)
                for dY, dX in dr:
                    newY = y + dY
                    newX = x + dX
                    if newY >= 0 and newY < m and newX >= 0 and newX < n and grid[newY][newX] == cl:
                        q.append([newY, newX])
                    elif str(newY) + ':' + str(newX) not in painted:
                        paint = True
                if paint:
                    painted.add(key)
                    grid[y][x] = color
        return grid
