class Solution {
    private final List<Integer[]> directions = List.of(new Integer[]{0,1},
            new Integer[]{0,-1}, new Integer[]{1,0}, new Integer[]{-1,0});
    private final Set<String> visited = new HashSet<>();
    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        if(grid[row][col] == color) return grid;
        dfs(row, col, grid[row][col], color, grid);
        return grid;
    }

    private void dfs(int row, int col, int componentColor, int newColor, int[][] grid) {
        visited.add(row + "," + col);
        if(isOnBorders(row, col, grid) || isAdjacentToDiffComponent(row, col, componentColor, grid)) 
            grid[row][col] = newColor;
        for (Integer[] direction: directions) {
            int i= row + direction[0], j = col + direction[1];
            if(isValidCoordinates(grid,i,j) && !visited.contains(i + "," + j) && grid[i][j] == componentColor)
                dfs(i, j, componentColor, newColor, grid);
        }
    }

    private boolean isAdjacentToDiffComponent(int row, int col, int componentColor, int[][] grid) {
        for (Integer[] direction: directions) {
            int i= row + direction[0], j = col + direction[1];
            if(isValidCoordinates(grid, i, j) && !visited.contains(i + "," + j)  && grid[i][j] != componentColor)
                return true;
        }
        return false;
    }

    private boolean isValidCoordinates(int[][] grid, int i, int j) {
        return i >= 0 && i < grid.length && j >= 0 && j < grid[0].length;
    }

    private boolean isOnBorders(int row, int col, int[][] grid) {
        return row == 0 || row == grid.length-1 || col == 0 || col == grid[0].length-1;
    }
}
