class Solution {
    int[][] ans;

    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        ans = new int[grid.length][];
        for(int i=0;i<grid.length;i++){
            ans[i] = grid[i].clone();
        }

        coloring(grid,row,col,grid[row][col],color,new int[grid.length]);

       return ans;
    }

    public boolean check(int n, int pos){
        int mask = 1<<pos;

        if((n&mask) == mask){
            return true;
        }

        return false;
    }

    public int toggle(int n, int pos){
        int mask = 1<<pos;
        return (n^mask);
    }

    public void coloring(int[][] arr, int row, int col, int oCol,int target, int[] visited){
        if(row<0 || row>=arr.length || col<0 || col>=arr[0].length || (check(visited[row],col) == true) || arr[row][col] != oCol){
            return;
        }

        if((row == 0 || row == arr.length-1 || col == 0 || col == arr[0].length-1 || (arr[row-1][col] != oCol || arr[row][col-1] != oCol || arr[row+1][col] != oCol || arr[row][col+1] != oCol)) && arr[row][col] == oCol){
            ans[row][col] = target;
        }

        visited[row] = toggle(visited[row], col);
        coloring(arr,row-1,col,oCol,target,visited);
        coloring(arr,row,col-1,oCol,target,visited);
        coloring(arr,row+1,col,oCol,target,visited);
        coloring(arr,row,col+1,oCol,target,visited);
        visited[row] = toggle(visited[row], col);
    }
}
