class Solution {
public:
int n,m;
int dx[4]={-1,0,1,0};
int dy[4]={0,1,0,-1};
vector<vector<int>>vis,ans;
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        n=grid.size();
        m=grid[0].size();
        ans.resize(n,vector<int>(m,0));
        vis.resize(n,vector<int>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans[i][j]=grid[i][j];
            }
        }
        queue<pair<int,int>>q;
        int c=grid[row][col];
        q.push({row,col});
        vis[row][col]=1;
        while(!q.empty()){
            int x=q.front().first;
            int y=q.front().second;
            q.pop();
            int cnt=0;
             for(int i=0;i<4;i++){
                 int nx=x+dx[i];
                 int ny=y+dy[i];
                 if(nx>=0 && nx<n && ny>=0 && ny<m && grid[nx][ny]==c){
                     cnt++;

                     if(!vis[nx][ny]){
                      q.push({nx,ny});
                      vis[nx][ny]=1;
                     }
                 }
             }

             if(cnt<4){
                 ans[x][y]=color;
             }
        }

        return ans;
    }
};
