class Solution {
    public int[][] colorBorder(int[][] arr, int row, int col, int color) {
        check(arr,row,col,arr[row][col]);

        for(int i=0;i<arr.length;i++){
            for(int j=0;j<arr[0].length;j++){
                if(arr[i][j]<0){
                    arr[i][j] = color;
                }
            }
        }
        return arr;
    }

    int[][] dir = {{0,1},{1,0},{0,-1},{-1,0}};
    public void check(int[][] arr,int row,int col,int clr){
        arr[row][col] = -clr;

        // check in 4 direction
        int count=0;
        for(int i=0;i<4;i++){
            // check coonected component;
            int r = row + dir[i][0];
            int c = col + dir[i][1];

            if(r<0 || r>=arr.length || c<0 || c>=arr[0].length || Math.abs(arr[r][c]) != clr){
                continue;
            }

            count++;
            if(arr[r][c]==clr){
                check(arr,r,c,clr);
            }
        }

        // check if all dir same or not
        if(count==4){
            arr[row][col]=clr;
        }
    }
}
