impl Solution {
    pub fn color_border(grid: Vec<Vec<i32>>, row: i32, col: i32, color: i32) -> Vec<Vec<i32>> {
        fn dfs(grid: &mut Vec<Vec<i32>>, r: i32, c: i32, cl: i32) {
            if r < 0
                || r >= grid.len() as i32
                || c < 0
                || c >= grid[r as usize].len() as i32
                || grid[r as usize][c as usize] != cl
            {
                return;
            }
            grid[r as usize][c as usize] = -cl;
            dfs(grid, r - 1, c, cl);
            dfs(grid, r + 1, c, cl);
            dfs(grid, r, c - 1, cl);
