class Solution {
public:
    int n , m;
    void markMinusOne(int i , int j , int color , vector<vector<int>> &grid){
        if(i<0 or j<0 or i>=n or j>=m) return;
        if(grid[i][j]!=color) return;

        grid[i][j] = -1;

        markMinusOne(i+1 , j , color, grid);
        markMinusOne(i-1 , j , color, grid);
        markMinusOne(i , j+1 , color, grid);
        markMinusOne(i , j-1 , color, grid);
    }



    bool isSurrounded(int i , int j , vector<vector<int>>&grid){
        if(i==0 or  j==0 or  i==n-1 or  j==m-1) return false;
        if(i!=0 && !(grid[i-1][j]==-1 or grid[i-1][j]==0)) return false;
        if(j!=0 && !(grid[i][j-1]==-1 or grid[i][j-1]==0)) return false;
        if(i!=n-1 && !(grid[i+1][j]==-1 or grid[i+1][j]==0)) return false;
        if(j!=m-1 && !(grid[i][j+1]==-1 or grid[i][j+1]==0)) return false;
        return true;
    }

    void undoInner(int i , int j ,int color ,vector<vector<bool>>&vis  ,vector<vector<int>>&grid){
        if(i<0 or j<0 or i>=n or j>=m) return;
        if(grid[i][j]!=-1 or vis[i][j]) return;
        vis[i][j] = true;

        if(isSurrounded(i , j , grid)){
            grid[i][j] = 0;
        }

        undoInner(i+1 , j , color, vis ,grid);
        undoInner(i-1 , j , color, vis ,grid);
        undoInner(i , j+1 , color,  vis ,grid);
        undoInner(i , j-1 , color, vis ,grid);

    }
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        int x = grid[row][col];
        n = grid.size();
        m = grid[0].size();
        // -1 -> with color x
        // 0 -> inner x within the component

        // marking the x as -1 in the connected component
        markMinusOne(row , col , x , grid);

        // marking the inner x as 0 in the connected component
        vector<vector<bool>> vis(n , vector<bool>(m , false));
        undoInner(row , col , x , vis ,grid);

        // coloring the -1 with color in the connected component and inner blocks as x
        for(int i=0 ; i<n ; i++){
            for(int j=0 ; j<m ; j++){
                if(grid[i][j]==-1)grid[i][j] = color;
                if(grid[i][j]==0) grid[i][j] = x;
            }
        }


        // returning the final grid

        return grid;

    }
};
