class Solution {
    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        boolean[][] visited = new boolean[grid.length][grid[0].length];
        return dfs(grid, row, col, color, visited);
    }
    public int[][] dfs(int[][] grid, int row, int col, int color, boolean[][] visited){
        visited[row][col] = true;
        boolean isBoundry = false;
        if(row == 0 || row == grid.length - 1 || col == 0 || col == grid[0].length - 1)
            isBoundry = true;
        int[] rd = {1, 0, -1, 0};
        int[] cd = {0, 1, 0, -1};
        for(int i = 0; i < 4; i++){
            int newRow = row + rd[i];
            int newCol = col + cd[i];
            if(isValid(grid, newRow, newCol) && !visited[newRow][newCol]){
                if(grid[newRow][newCol] == grid[row][col])
                    dfs(grid, newRow, newCol, color, visited);
                else isBoundry = true;
            }
        }
        if(isBoundry) grid[row][col] = color;
        return grid;
    }
    public boolean isValid(int[][] grid, int row, int col){
        return row >= 0 && row < grid.length && col >= 0 && col < grid[0].length;
    }
