    const borderCells = []

    const componentColor = grid[row][col]
    return function(){
        const queue = [{row,col}]

        while (queue.length) {
            const currentPos = queue.shift();
            visited[`${currentPos.row}|${currentPos.col}`] = true

            if (isABorderCell(currentPos, grid)) {
                borderCells.push(currentPos);
            }

            queue.push(
                ...getNeighbouringCells(currentPos, grid).filter(
                    pos =>
                        Boolean(pos) &&
                        visited[`${pos.row}|${pos.col}`] !== true &&
                        grid[pos.row][pos.col] === componentColor
                )
            );
        }

        return borderCells
    }
}

function isABorderCell({ row, col }, grid) {
  return (
    row === 0 ||
    col === 0 ||
    row === grid.length - 1 ||
    col === grid[0].length - 1 ||
    (
        grid[row - 1][col] !== grid[row][col]
        || grid[row + 1][col] !== grid[row][col]
        || grid[row][col - 1] !== grid[row][col]
        || grid[row][col + 1] !== grid[row][col]
    )
  );
}

function getNeighbouringCells({ row, col }, grid){
    return [
        getNextPos("l", { row, col }, grid),
        getNextPos("r", { row, col }, grid),
        getNextPos("u", { row, col }, grid),
        getNextPos("d", { row, col }, grid),
    ]
}

function getNextPos(dir, { row, col }, grid) {
  if (dir === "l") {
    if (col - 1 >= 0) return { row, col: col - 1 };
  } else if (dir === "r") {
    if (col + 1 < grid[0].length) return { row, col: col + 1 };
  } else if (dir === "u") {
    if (row - 1 >= 0) return { row: row - 1, col };
  } else if (dir === "d") {
    if (row + 1 < grid.length) return { row: row + 1, col };
  }
}
