class Pair {
    int row;
    int col;

    public Pair(int row, int col)
    {
        this.row = row;
        this.col = col;
    }
}
class Solution {
    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        int r = grid.length;
        int c = grid[0].length;
        int vis[][] = new int [r][c];
        Queue<Pair> queue = new LinkedList<>();
        queue.add(new Pair(row , col));
        vis[row][col]=1;
        int val = grid[row][col];
        int ans[][] = new int[r][c];

        for(int i=0; i< r; i++)
        {
            for(int j=0; j< c ; j++)
            {
                ans[i][j]= grid[i][j];
            }
        }


        int delRow [] = {-1,0,0,1};
        int delCol [] = {0,1,-1,0};


        while(!queue.isEmpty())
        {
            int qr = queue.peek().row;
            int qc = queue.peek().col;
            queue.remove();
            int cnt = 0;
            for(int i=0; i<4 ; i++)
            {
                int nrow = qr + delRow[i];
                int ncol = qc + delCol[i];

                if(nrow >=0 && ncol >=0 && nrow < r && ncol < c && vis[nrow][ncol] != 1 && grid[nrow][ncol] == val)
                {
                  queue.add(new Pair(nrow, ncol));
                  vis[nrow][ncol] =1;

                }
                if(nrow >=0 && ncol >=0 && nrow < r && ncol < c && grid[nrow][ncol] == val)
                {
                  cnt++;              
                }
            }
            if(cnt != 4)
            {
               ans[qr][qc] = color;
            }

        }

        return ans;
    }
}
