class Solution {
public: 
bool solve(int i,int j,vector<vector<int>>&grid,int color,int n,int m,int t,vector<vector<bool>>&visited)
{
    if(i<0||j<0||i==n||j==m||grid[i][j]!=t||visited[i][j])
    return false;
    visited[i][j]=true;
    bool bod=false;
    if(i==0||j==0||i==n-1||j==m-1||grid[i-1][j]!=t||grid[i+1][j]!=t||grid[i][j-1]!=t||grid[i][j+1]!=t)
    bod=true ;
    solve(i-1,j,grid,color,n,m,t,visited);
    solve(i+1,j,grid,color,n,m,t,visited);
    solve(i,j-1,grid,color,n,m,t,visited);
    solve(i,j+1,grid,color,n,m,t,visited);
