class Solution {
    bool isBoundary(vector<vector<int>>& grid, int row, int col, const int& ROWS, const int& COLS, const int& myColor) {
        if (row < 0 || col < 0 || row >= ROWS || col >= COLS) {
            return true;
        } else if (grid[row][col] != myColor) {
            return true;
        }
        return false;
    }

    void markBoundary(vector<vector<int> >& grid, int row, int col, const int& ROWS, const int& COLS, int color, const int& myColor, vector<vector<int>>& maze) {
        if (isBoundary(grid, row, col - 1, ROWS, COLS, myColor)) {
            maze[row][col] = color;
            return;
        }
        if (isBoundary(grid, row, col + 1, ROWS, COLS, myColor)) {
            maze[row][col] = color;
            return;
        }
        if (isBoundary(grid, row - 1, col, ROWS, COLS, myColor)) {
            maze[row][col] = color;
            return;
        }
        if (isBoundary(grid, row + 1, col, ROWS, COLS, myColor)) {
            maze[row][col] = color;
            return;
        }
    }

    bool isSafe(vector<vector<int>>& grid, int row, int col, const int& ROWS, const int& COLS, int myColor, vector<vector<bool> >& visited) {
        if (row >= 0 && col >= 0 && row < ROWS && col < COLS && grid[row][col] == myColor && !visited[row][col]) {
            return true;
        }
        return false;
    }

    void colorGrid(vector<vector<int>>& grid, int row, int col, const int& ROWS, const int& COLS, int color, const int& myColor, vector<vector<bool> >& visited, vector<vector<int> >& maze) {
        visited[row][col] = true;
        markBoundary(grid, row, col, ROWS, COLS, color, myColor, maze);
        if (isSafe(grid, row - 1, col, ROWS, COLS, myColor, visited)) {
            colorGrid(grid, row - 1, col, ROWS, COLS, color, myColor, visited, maze);
        }
        if (isSafe(grid, row + 1, col, ROWS, COLS, myColor, visited)) {
            colorGrid(grid, row + 1, col, ROWS, COLS, color, myColor, visited, maze);
        }
        if (isSafe(grid, row, col - 1, ROWS, COLS, myColor, visited)) {
            colorGrid(grid, row, col - 1, ROWS, COLS, color, myColor, visited, maze);
        }
        if (isSafe(grid, row, col + 1, ROWS, COLS, myColor, visited)) {
            colorGrid(grid, row, col + 1, ROWS, COLS, color, myColor, visited, maze);
        }

    }
public:
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        const int ROWS = grid.size();
        const int COLS = grid[0].size();
        vector<bool> vc(COLS, false);
        vector<vector<bool> > visited(ROWS, vc);
        const int myColor = grid[row][col];
        vector<vector<int> > maze = grid;
        colorGrid(grid, row, col, ROWS, COLS, color, myColor, visited, maze);
        return maze;
    }
};
