class Solution {
public:
    void recursive(vector<vector<int>>& grid, vector<vector<int>>& ans, int row, int col, int color, int comp, vector<vector<int>>& visited){
        if(row>=grid.size() || row<0 || col>=grid[0].size() || col<0 || grid[row][col]!=comp || visited[row][col]) return;

        visited[row][col] = 1;
        if(row<1 || col<1 || row==grid.size()-1 || col==grid[0].size()-1 || grid[row+1][col]!=comp || grid[row-1][col]!=comp || grid[row][col+1]!=comp || grid[row][col-1]!=comp)
        ans[row][col] = color;

        recursive(grid, ans, row+1, col, color, comp, visited);
        recursive(grid, ans, row-1, col, color, comp, visited);
        recursive(grid, ans, row, col+1, color, comp, visited);
        recursive(grid, ans, row, col-1, color, comp, visited);
    }
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        int component = grid[row][col];
        vector<vector<int>> ans = grid;
        vector<vector<int>> visited(grid.size(), vector<int>(grid[0].size(), 0));
        recursive(grid, ans, row, col, color, component, visited);
        return ans;
    }
};
