class Solution 
{
public:
    int rows, cols;
    vector<vector<int>> ans;
    bool isValid(int r, int c)
    {
        if(r<0 || c<0 || r==rows || c==cols) return false;
        return true;
    }
    void dfs(vector<vector<int>>& grid, vector<vector<int>> &vis, int co, int r, int c, int color)
    {
        vis[r][c]=1;
        vector<vector<int>> directions={{-1,0},{0,-1},{1,0},{0,1}};
        for(int x=0; x<4; x++)
        {
            int dr=r+directions[x][0], dc=c+directions[x][1];
            if(isValid(dr,dc) && grid[dr][dc]==co) 
            {
                if(!vis[dr][dc]) dfs(grid,vis,co,dr,dc,color);
            }
            else if(!isValid(dr,dc) || (isValid(dr,dc) && grid[dr][dc]!=co)) ans[r][c]=color;
        }
    }
