        ans=grid;
        int co=grid[row][col];
        rows=grid.size(), cols=grid[0].size();
        vector<vector<int>> vis(rows, vector<int> (cols,0));
        dfs(grid,vis,co,row,col,color);
        return ans;
    }
};
