    void dfs(int row,int col,int c,vector<vector<int>>& grid,vector<vector<int>>& vis,int m,int n,int color,vector<vector<int>>&ans)
    {
        vis[row][col]=1;
        int di[]={-1,1,0,0};
        int dj[]={0,0,-1,1};
        for(int i=0;i<4;i++)
        {
