class Solution {
    public class Node{
        int i;
        int j;
        public Node(int ii,int jj)
        {
            i=ii;
            j=jj;
        }
    }
    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        int comp=grid[row][col];
        boolean[][] visited=new boolean[grid.length][grid[0].length];
        int[][] dir={{-1,0,1,0},{0,-1,0,1}};
        LinkedList<Node> li=new LinkedList<>();
        li.addFirst(new Node(row,col));
        while(li.size()!=0)
        {
            Node temp=li.removeFirst();
            visited[temp.i][temp.j]=true;
            for(int z=0;z<dir[0].length;z++)
            {
                int i=temp.i+dir[0][z];
                int j=temp.j+dir[1][z];
                if(i<0 || j<0 || i==grid.length || j==grid[0].length || grid[i][j]!=comp && !visited[i][j])
                {
                    grid[temp.i][temp.j]=color;
                    continue;
                }
                if(!visited[i][j])
                {
                    li.addFirst(new Node(i,j));
                }
            }
        }
        return grid;
    }
}
