class Solution {
bool isValid(int r, int c, vector<vector<int>>& matrix) {
	return ((r >= 0 && r < (int)matrix.size()) && (c >= 0 && c < (int)matrix[0].size()));
}

void dfs_Color(int r, int c, vector<vector<int>>& matrix, vector<vector<bool>>& visited, int oldColour, int newColour) {
		if (!isValid(r, c, matrix) || visited[r][c] || matrix[r][c] != oldColour)
			return;

    visited[r][c] = true; matrix[r][c] = newColour;

		int dr[] = { -1, 0 , 1 , 0 }; // up , right , down , left
		int dc[] = { 0 , 1 , 0 , -1 };
		for (auto d = 0; d < 4; ++d)
			dfs_Color(r + dr[d], c + dc[d], matrix, visited, oldColour, newColour);
}

public:
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
       vector<vector<bool>>visited(grid.size(), vector<bool>(grid[0].size()));
				vector<vector<int>>temp(grid);
				int oldcolor = grid[row][col];

				dfs_Color(row, col, grid, visited, grid[row][col], color);

				int dr[] = { -1, 0 , 1 , 0 };
				int dc[] = { 0 , 1 , 0 , -1 };

				for (auto i = 0; i < (int)grid.size(); ++i) {
					for (auto j = 0; j < (int)grid[i].size(); ++j) {
						int cnt = 0;

						if (temp[i][j] == oldcolor) {

							for (auto d = 0; d < 4; ++d) {

								if (isValid(i + dr[d], j + dc[d], temp)) {
									if (temp[i + dr[d]][j + dc[d]] == oldcolor) ++cnt;
								}

							}
						}
						if (cnt == 4) {
							grid[i][j] = oldcolor;
						}
					}
				}
				return grid;
    }
};

 `class Solution {
public:
    int n, m;
