        if (color == originalColor)
            return grid;
        dfs(row, col, grid, originalColor);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] < 0)
                    grid[i][j] = color;
            }
        }
        return grid;
    }
