class Solution {

    private static void dfs(int row, int col, int Row, int Col, int[] dRow, int[] dCol,
    int srcColor, int[][] grid, int[][] visited){
        visited[row][col] = 1;
        for(int move=0;move<4;move++){
            int row_ = row + dRow[move], col_ = col + dCol[move];
            if(row_>=0 && col_>=0 && row_<Row && col_<Col){
                if(grid[row_][col_]==srcColor && visited[row_][col_]==0){
                    visited[row_][col_] = 1;
                    dfs(row_,col_,Row,Col,dRow,dCol,srcColor,grid,visited);
                }
            }
        }
    }

    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        int Row = grid.length, Col = grid[0].length, srcColor = grid[row][col];
        int[][] visited = new int[Row][Col];
        int[] dRow = new int[]{-1,0,1,0}, dCol = new int[]{0,1,0,-1};
        dfs(row,col,Row,Col,dRow,dCol,srcColor,grid,visited);
        for(int i=0;i<Row;i++){
            for(int j=0;j<Col;j++){
                if(visited[i][j]==1){
                    if(i==0||i==Row-1||j==0||j==Col-1){ visited[i][j] = color; }
                    else{
                        boolean isBoundaryCell = false;
                        for(int move=0;move<4;move++){
                            int i_ = i + dRow[move], j_ = j + dCol[move];
                            if(grid[i_][j_]!=srcColor) isBoundaryCell = true;
