 `class Solution {
public:
    int n, m;
    int delr[4] = {0, 0, 1, -1};
    int delc[4] = {1, -1, 0, 0};

    void dfs(int r, int c, vector<vector<int>>& grid, int color) {
        grid[r][c] = -color;
        for (int k = 0; k < 4; k++) {
            int nr = r + delr[k];
            int nc = c + delc[k];
            if (nr < 0 || nc < 0 || nr >= m || nc >= n || grid[nr][nc] != color)
                continue;
            dfs(nr, nc, grid, color);
        }
        if (r > 0 && r < m - 1 && c > 0 && c < n - 1) {
            if (color == abs(grid[r + 1][c]) && color == abs(grid[r - 1][c]) && color == abs(grid[r][c + 1]) && color == abs(grid[r][c - 1]))
                grid[r][c] = color;
        }
    }

    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        m = grid.size();
        n = grid[0].size();
        int originalColor = grid[row][col];

        if (color == originalColor)
            return grid;
        dfs(row, col, grid, originalColor);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] < 0)
                    grid[i][j] = color;
            }
        }
        return grid;
    }
};`


