  public String minInteger(String num, int k) {
    Queue<Integer>[] qArr = new ArrayDeque[10];
    char[] arr = num.toCharArray();
    int n = num.length();

    for (int d = 0; d < 10; d++) {
      qArr[d] = new ArrayDeque<>();
    }

    for (int i = 0; i < n; i++) {
      qArr[arr[i] - '0'].offer(i);
    }
    TreeSet<Integer> treeSet = new TreeSet<>();

    StringBuilder res = new StringBuilder();
    for (int i = 0; i < n; i++) {
      for (int d = 0; d < 10; d++) {
        Queue<Integer> q = qArr[d];
        if (q.isEmpty()) {
          continue;
        }
        int idx = q.peek();
        int numLessOrEqual = treeSet.headSet(idx).size();
        int requiredSteps = idx - numLessOrEqual; 
        if (k >= requiredSteps) {
          q.poll();
          k -= requiredSteps;
          res.append(d);
          treeSet.add(idx);
          break;
        }
      }
    }

    return res.toString();

  }
