I applied the greedy approach where I will move the smallest possible digit to the leftmost unfixed position. Instead of updating all the ununsed indices to the left, I used a segement tree to find the number of digits on the right side of each digit which crossed over the current digit and go left. This brought down the complexity to O(nlogn).

    void update_seg_tree(vector<int>& seg_tree,int idx,int sidx,int ssrt, int send)
    {
        if(idx<ssrt or idx>send)    return;

        if(ssrt==send)
        {
            seg_tree[sidx]=1;
            return;
        }

        int smid=(ssrt+send)/2;
        update_seg_tree(seg_tree,idx,2*sidx+1,ssrt,smid);
        update_seg_tree(seg_tree,idx,2*sidx+2,smid+1,send);

        seg_tree[sidx]=seg_tree[2*sidx+1]+seg_tree[2*sidx+2];
    }

    int calcte_seg_tree(vector<int>& seg_tree,int srt,int end,int sidx,int ssrt, int send)
    {
        if(end<ssrt or srt>send)        return 0;

        if(ssrt>=srt and send<=end)     return seg_tree[sidx];

        int smid=(ssrt+send)/2;
        int left = calcte_seg_tree(seg_tree,srt,end,2*sidx+1,ssrt,smid);
        int rigt = calcte_seg_tree(seg_tree,srt,end,2*sidx+2,smid+1,send);

        return left+rigt;
    }

public:
    string minInteger(string num, int k) {

        int n = num.size();

        //Calculating the height of the segment tree(starting from 0)
        int height=0;

        int p=n-1;
        while(p)
        {
            p/=2;
            height++;
        }

        //For a tree of height h, the total number of nodes till height h is 2*(2^h)-1
        vector<int> seg_tree(2*(1<<height)-1,0);

        list<int> lst[10];

        for(int i=0;i<n;i++)
        {
            lst[num[i]-'0'].push_back(i);
        }

        string ans;

        for(int i=0;i<n;i++)
        {
            int digit=-1;
            int kminus=0;
            for(int j=0;j<10;j++)
            {
                if(!lst[j].empty())
                {
                    int pos=lst[j].front();
                    int right_shift=calcte_seg_tree(seg_tree,pos,n-1,0,0,n-1);

                    if(pos+right_shift-i<=k)
                    {
                        kminus=pos+right_shift-i;
                        digit=j;
                        break;
                    }
                }
            }

            ans.push_back('0'+digit);

            update_seg_tree(seg_tree,lst[digit].front(),0,0,n-1);

            k-=kminus;

            lst[digit].pop_front();
        }

        return ans;
    }

