class SegmentTree {
	public:
	vector<int> arr;
	int n;
	SegmentTree(int n) {
		this->n=n;
		arr = vector<int>(4*n);
	}
	void add(int i) {
		addUtil(0,n-1,i,0);
	}

	int addUtil(int l, int r, int ind, int node) {
		if(l == r && l == ind){
			arr[node]++;
			return arr[node];
		}
		if(ind < l || ind > r)
			return arr[node];
		int m = l + (r-l)/2;
		int le = addUtil(l,m,ind,2*node+1);
		int ri = addUtil(m+1,r,ind,2*node+2);
		arr[node] = le+ri;
		return arr[node];
	}

	int getSum(int ql, int qr, int l, int r, int node) {
		if(l >= ql && r <= qr)
			return arr[node];
		if(r < ql || l > qr)
			return 0;
		int m = l + (r-l)/2;
		return arr[node] = getSum(ql,qr,l,m,2*node+1) + getSum(ql,qr,m+1,r,2*node+2);
	}
};

class Solution {
public:
	string minInteger(string num, int k) {
		int n = num.size();
		vector<queue<int>> arr(10);
		SegmentTree *tree = new SegmentTree(n);

		for(int i = 0; i < n; i++) {
			arr[num[i]-'0'].push(i);
		}

		string ans;
		for(int i = 0; i < num.size(); i++) {
			for(int digit = 0; digit <= 9; digit++) {
				if(arr[digit].size() != 0) {
					int pos = arr[digit].front();
					int shift = tree->getSum(0,pos,0,n,0);
					if(pos-shift <= k) {
						k -= pos-shift;
						tree->add(pos);
						arr[digit].pop();
						ans.push_back(digit+'0');
						break;
					}
				}
			}
		}

		return ans;
	}
};

