template <class T>
class simple_queue    
{
    int out_ix;
    vector<T> q;
public:
    simple_queue() : out_ix(0) {}
    ~simple_queue()  { /* delete [] q;*/ }
    void push(T t)   { q.push_back(t);  }
    T    pop(void)   { return q[out_ix++]; }
    T    front(void) { return q[out_ix]; }
    bool empty(void) { return (out_ix == q.size());}
};
class Solution {
public:
    string minInteger(string num, int k) {
        int n = num.length();
        string ans(n,'-');
        ans[n] = '\0';
        int i = 0;
        int first = 0;
        int start = 0;
        bool found = true;
        vector<simple_queue<int>> pos(10);
        vector<int> first_pos_sub(10,0);

        for(int j=0; j<n; j++)
            pos[num[j]-'0'].push(j);

        while (found == true && i<n)
        {
            found = false;
            while(first<=9 && pos[first].empty())  first++;  
            for (int d = first; d<=9 && !found; d++)
            {
                while (!pos[d].empty() && pos[d].front()  - first_pos_sub[d]  <= k)
                {
                    // cout << d << ", " << k << ", " << pos[d].front()- first_pos_sub[d] << endl;
                    found = true;
                    int p = pos[d].pop();
                    k -= (p -first_pos_sub[d]);
                    ans[i++] = '0'+d;
                    for (int d1 = first; d1<=9; d1++)
                    {
                        if (!pos[d1].empty() && pos[d1].front() > p) first_pos_sub[d1]++;
                    }
                    num[p] = '-';
                    if (!pos[d].empty())
                    {
                        for (int j=p+1; j<pos[d].front(); j++)
                        {
                            if (num[j] == '-') first_pos_sub[d]++;
                        }
                    }

                    if (d != first) break;
                }
            }
        }
        return ans;
    }
};
