class Solution {
    public static String minInteger(String num, int k) {
        char[] array = num.toCharArray(); // returns a newly allocated character array.
        int length = array.length;
        int pointer = 0; // the index replaced at most K times by the smallest possible digit
        while (k > 0 && pointer < length) {
            int minDigit = array[pointer] - '0', minIndex = pointer;
            int limit = Math.min(length - 1, pointer + k); // when k is too large -> pointer + k is too large, run out
                                                           // of range -> min(length-1, pointer+k)
            for (int i = pointer + 1; i <= limit; i++) {
                // take smallest digit from position: pointer -> limit
                int temp = array[i] - '0';
                if (temp < minDigit) {
                    minDigit = temp; // smallest digit
                    minIndex = i; // smallest digit's index
                }
            }
            k -= (minIndex - pointer); // swapping times needed to move smallest digit to pointer position
            char c = array[minIndex]; // smallest digit: c (char)
            // move c = array[minIndex] to be array[pointer]: line 21 -> 23
            for (int i = minIndex - 1; i >= pointer; i--)
                array[i + 1] = array[i];
            array[pointer] = c;
            pointer++; // continue with next array[pointer]
        }
        return new String(array);
    }
}
