struct Node {
    int l, r;
    int v;
    Node() {
        v = 0;
    };
};


class Solution {
public:
    vector<Node> tree;
    void build(int l, int r, int p) {
        tree[p].l = l;
        tree[p].r = r;
        if (l != r) {
            int mid = (l + r) >> 1;
            build(l, mid, 2 * p);
            build(mid+1, r, 2 * p + 1);
        }
    }
    void insert(int x, int p) {
        tree[p].v--;
        if (tree[p].l != tree[p].r) {
            int mid = (tree[p].l + tree[p].r) >> 1;
            if (x <= mid) insert(x, 2*p);
            else insert(x, 2*p+1);
        }
    }
    int query(int l, int r, int p) {
        if (tree[p].l == l && tree[p].r == r) return tree[p].v;
        else {
            int mid = (tree[p].l + tree[p].r) >> 1;
            if (r <= mid) return query(l, r, 2*p);
            else if (l >= mid+1) return query(l, r, 2*p + 1);
            else return query(l, mid, 2*p) + query(mid+1, r, 2*p + 1);
        }
    }

    string minInteger(string num, int k) {
        string result;

        vector<int> offset(num.size(), 0);
        vector<deque<int>> pos(10);
        for (int i = 0; i < num.size(); i++) pos[num[i]-'0'].push_back(i);

        tree = vector<Node>(4 * num.size() + 10);
        build(0, num.size() - 1, 1);

        int i = 0;
        while(true) {
            while (i < num.size() && offset[i] < 0) i++;
            if (i >= num.size()) break;

            int cp = i;
            if (cp - 1 >= 0) cp += query(0, cp - 1, 1);

            for (int p = 0; p <= 9; p++) {
                if (pos[p].size() > 0) {

                    int fp = pos[p].front();
                    if (fp - 1 >= 0) fp += query(0, fp - 1, 1);

                    if (fp - cp <= k) {
                        k -= fp - cp;
                        offset[pos[p].front()] = -1;
                        insert(pos[p].front(), 1);

                        pos[p].pop_front();
                        result += char('0' + p);
                        break;
                    }
                }
            }
        }
        return result;
    }
};
