In short, the idea is to bubble up the smallest element so far (need to consider k swaps constraint)

	public class Solution1505 {
		public static void main(String[] args) {
			String num = "9438957234785635408";
			int k = 23;

			Solution1505 solution1505 = new Solution1505();

			System.out.println(solution1505.minInteger(num, k));


		}

		public String minInteger(String num, int k) {
			StringBuilder sb = new StringBuilder();
			char[] input = num.toCharArray();

			int idx = 0;

			while (idx < input.length && k > 0)
			{
				int minValue = input[idx] - '0';
				int minIndex = idx;

				int j = idx + 1;

				int tempK = k;
				while (j < input.length && tempK > 0)
				{
					int curValue = input[j] - '0';
					if (curValue < minValue) {
						minValue = curValue;
						minIndex = j;
					}
					j++;
					tempK--;
				}

				sb.append(input[minIndex]);

				for (int i = minIndex; i > idx; --i)
				{
					swap(input, i, i - 1);
					k--;
				}

				idx++;
			}

			for (int i = idx; i < input.length; ++i)
			{
				sb.append(input[i]);
			}

			return sb.toString();

		}

		private void swap(char[] input, int i, int j)
		{
			char temp = input[i];

			input[i] = input[j];

			input[j] = temp;
		}
	}

