class Solution {
    public String minInteger(String num, int k) {
        char[] ca = num.toCharArray();
        helper(ca, 0, k);
        return new String(ca);
    }

    public void helper(char[] ca, int I, int k) {
        if(k==0 || I==ca.length)
            return ;
        int min=ca[I], minindex=I;
        for(int i=I+1;i<Math.min( I+k+1, ca.length);i++) {

            if(ca[i]<min) {
                min=ca[i];
                minindex=i;
            }
        }
        char temp=ca[minindex];

        for(int i=minindex;i>I;i--) {
            ca[i]=ca[i-1];
        }
        ca[I]=temp;
        helper(ca, I+1, k-(minindex-I));
    }
}
