class FenwickTree
{
 public:
    explicit FenwickTree(int size)
        : arr(size + 1, 0)
    {
    }

    int get(int index)
    {
        ++index;
        int value = 0;

        while (index)
        {
            value += arr[index];
            index -= index & (-index);
        }

        return value;
    }

    void set(int index)
    {
        ++index;

        while (index < arr.size())
        {
            ++arr[index];
            index += index & (-index);
        }
    }

private:
    vector<int> arr;
};

class Solution {
public:

    string minInteger(string num, int k) 
    {
        vector<deque<int>> indexes(10, deque<int>());
        string result;
        FenwickTree tree(num.size());

        for (int i = 0; i < num.size(); ++i)
            indexes[num[i] - '0'].push_back(i);

        for (int i = 0; i < num.size(); ++i)
        {
            auto digit = num[i] - '0';

            if (indexes[digit].empty() || indexes[digit].front() != i)
                continue;

            indexes[digit].pop_front();

            for (int d = 0; d < digit; ++d)
            {
                if (!indexes[d].empty() && indexes[d].front() - i - 
                    tree.get(indexes[d].front()) + tree.get(i) <= k)
                {
                    k -= indexes[d].front() - i - 
                        tree.get(indexes[d].front()) + tree.get(i);

                    tree.set(indexes[d].front());

                    indexes[d].pop_front();
                    result += (char)(d-- + '0');
                }
            }

            result += num[i];
        }

        return result;
    }
};
