class Solution {
    public String minInteger(String num, int k) {
        int n = num.length();
        FenwickTree ft = new FenwickTree(n);
        for(int i = 0; i < n; i++) {
            ft.update(i, 1);
        }
        Queue[] arr = new Queue[10];
        for(int i = 0; i < 10; i++) arr[i] = new LinkedList<>();
        for(int i = 0; i < n; i++) {
            arr[num.charAt(i) - '0'].offer(i);
        }
        int idx = 0;
        String res = "";
        while(idx < n) {
            for(int i = 0; i < 10; i++) {
                if(arr[i].size() > 0) {
                    int bestIdx = (int)arr[i].peek();
                    int cost = ft.getSum(bestIdx) - 1;
                    if(cost <= k) {
                        res += (char)('0' + i);
                        arr[i].poll();
                        k -= cost;
                        if(bestIdx < n - 1) ft.update(bestIdx + 1, -1);
                        break;
                    } else continue;
                }
            }
            idx += 1;
        }
        return res;  
    }
}

class FenwickTree {
    int[] tree;
    public FenwickTree(int n) {
        this.tree = new int[n + 1];
    }

    private int getNext(int x) {
        return x + (x & (-x));
    }

    private int getParent(int x) {
        return x - (x & (-x));
    }

    public void update(int idx, int diff) {
        idx += 1;
        while(idx < tree.length) {
            tree[idx] += diff;
            idx = getNext(idx);
        }
        return;
    }

    public int getSum(int idx) {
        idx += 1;
        int sum = 0;
        while(idx > 0) {
            sum += tree[idx];
            idx = getParent(idx);
        }
        return sum;
    }
}
