class FenwickTree {
private:
    vector<int> arr; 

public:
    FenwickTree(int n) {
        n++;
        arr = vector<int>(n, 0);
    }

    void add(int index, const int num) {
        index++;
        while (index < static_cast<int>(arr.size())) {
            arr[index] += num;
            index += (~index + 1) & index; 
        }
    }

    int getPrefixSum(int index) const {
        int sum = 0;
        index++;
        while (index > 0) {
            sum += arr[index];
            index -= (~index + 1) & index; 
        }
        return sum;
    }
};

class Solution {
public:
    string minInteger(string num, int k) {
        int n = num.length();
        FenwickTree tree(n);
        vector<int> numArr(n);
        for (int i = 0; i < n; i++) {
            numArr[i] = num[i] - '0';
        }
        unordered_map<int,vector<int>> indexMap;
        for (int i = 0; i < n; i++) {
            if (indexMap.count(numArr[i]) == 0) {
                indexMap[numArr[i]] = {i};
            }
            else {
                indexMap[numArr[i]].push_back(i);
            }
        }
        for (int i = 0; i < 10; i++) {
            if (indexMap.count(i) != 0) {
                reverse(indexMap[i].begin(), indexMap[i].end());
            }
        }
        string res = "";
        while (k > 0) {
            int i;
            for (i = 0; i < 10; i++) {
                if (indexMap.count(i) == 0) {
                    continue;
                }
                int leftMostIndex = indexMap[i].back();
                int usedIndices = tree.getPrefixSum(leftMostIndex);
                int rank = leftMostIndex - usedIndices;
                if (rank <= k) {
                    k -= rank;
                    res.push_back(i + '0');
                    indexMap[i].pop_back();
                    if (indexMap[i].empty()) {
                        indexMap.erase(i);
                    }
                    tree.add(leftMostIndex, 1);
                    break;
                }
            }
            if (i == 10) {
                break;
            }
        }
        vector<vector<int>> remaining;
        for (auto& [digit,row] : indexMap) {
            for (int i : row) {
                remaining.push_back({i,digit});
            }
        }
        sort(remaining.begin(), remaining.end(), 
            [](const vector<int>& a, const vector<int>& b) -> bool
        { 
            return a[0] < b[0]; 
        });
        for (vector<int>& entry : remaining) {
            res.push_back(entry[1] + '0');
        }
        return res;
    }
};
