class Solution {
public:
    string minInteger(string num, int k) {
        std::vector<std::vector<int>> digit_to_inds(10);
        std::vector<int> voids; // locations that are empty, no price to pay
        for (int i = num.size() - 1; i >= 0; --i) {
            digit_to_inds[static_cast<int>(num[i] - '0')].push_back(i);
        }       
        constexpr char VOID_MARK = 'X';
        int reserved = k;
        std::vector<int> res;
        while ((reserved > 0) && (res.size() < num.size())) {
            for (int d = 0; d <= 9; ++d) {
                auto &inds = digit_to_inds[d];
                if (inds.empty()) {
                    continue;
                }
                const int i = inds.back(); // smallest index for `d`
                // There were `j` elements prior to the index `i`
                // removed from `num`. There's no need to swap with them.
                const int j = std::distance(
                    voids.begin(), 
                    std::lower_bound(voids.begin(), voids.end(), i)
                );
				// Number of swap ops needed to bubble this to top.
                const int price = i - 0 - j;
                if (price > reserved) {
                    continue;
                }
                // Housekeeping.
                reserved -= price;
                res.push_back(d);
                voids.insert(voids.begin() + j, i);      
                num[i] = VOID_MARK;
                inds.pop_back();
                break;
            }
        }
		// Results come from the elements pushed to `res`, 
		// as well as the remaining ones in `num`.
        std::stringstream ss;
        for (int d : res) {
            ss << d;
        }
        for (char ch : num) {
            if (ch != VOID_MARK) {
                ss << ch;
            }
        }
        return ss.str();
    }
};
```

