class Solution:
	def minInteger(self, num: str, k: int) -> str:

		num = list(num)
		small = sorted(list(num))

		if k > len(num) ** 2:
			return ''.join(small)

		d = defaultdict(list)
		for i in range(len(num)):
			d[num[i]].append(i)

		indexs = []
		for char in sorted(d.keys()):
			indexs += d[char]

		done = []
		head = 0

		while k > 0 and indexs:
			if num == small:
				return ''.join(num)
			for i in range(len(indexs)):
				pre = bisect.bisect(done, indexs[i])
				newi = indexs[i] + len(done) - pre
				if newi >= head and newi - head <= k:
					bisect.insort(done, indexs[i])
					num = num[:head] + [num[newi]] + num[head:newi] + num[newi + 1:]
					k -= newi - head
					head += 1
					indexs.pop(i)
					break

		return ''.join(num)

