class Solution {
public:
    string ans;
    int tree[12*10001],lazy[12*10001],count;
    void Lazy(int id)
    {
        tree[id*2]+=lazy[id];
        lazy[id*2]+=lazy[id];
        tree[id*2+1]+=lazy[id];
        lazy[id*2+1]+=lazy[id];
        lazy[id]=0;
    }
    void update(int id,int l,int r,int x,int y)
    {
        if(l>y||r<x) return;
        if(l>=x&&r<=y)
        {
            tree[id]++;
            lazy[id]++;
            return;
        }
        int mid=(l+r)/2;
        Lazy(id);
        update(id*2,l,mid,x,y);
        update(id*2+1,mid+1,r,x,y);
        tree[id]++;
    }
    int query(int id,int l,int r,int x)
    {
        if(l>x||r<x) return 0;
        if(l==r) return tree[id];
        int mid=(l+r)/2;
        Lazy(id);
        return query(id*2,l,mid,x)+query(id*2+1,mid+1,r,x);
    }
    string minInteger(string & num, int k) {
        vector<queue<int>> A(10);
        vector<int> kt(num.size(),0);
        for(int i=0;i<=num.size()-1;i++)
            A[int(num[i])-'0'].push(i);
        for(int i=0;i<=num.size()-1;i++)
        {
            if(k==0) break;
            for(int j=0;j<=9;j++)
            {
                if(A[j].empty()!=0) continue;
                count=A[j].front()+query(1,0,num.size()-1,A[j].front())-i;
                if(count<=k)
                {
                    kt[A[j].front()]=1;
                    ans+=num[A[j].front()];
                    k=k-count;
                    update(1,0,num.size()-1,0,A[j].front());
                    A[j].pop();
                    break;
                }
            }
        }
        for(int i=0;i<=num.size()-1;i++)
        {
            if(kt[i]==0) ans+=num[i];
        }
        return ans;
    }
};
