class Solution {
public:

    int Do(int a,int b,string &s){
        if(a==s.size())return b;
        if(b==s.size())return a;
        if(s[a]<=s[b])return a;
        return b;
    }

    void build(vector<int> &seg,int si,int sl,int sr,string &s){
        if(sl==sr){
            seg[si]=sl;
            return;
        }

        int mid=(sl+sr)/2;
        build(seg,2*si,sl,mid,s);
        build(seg,2*si+1,mid+1,sr,s);
        seg[si]=Do(
            seg[si*2],
            seg[si*2+1],
            s
        );

    }


    int Q(vector<int> &seg,int si,int sl,int sr,int l,int r,string &s){
        if(l>r)return s.size();
        if(sl==l and sr==r)return seg[si];
        int mid=(sl+sr)/2;
        return Do(
            Q(seg,si*2,sl,mid,l,min(mid,r),s),
            Q(seg,si*2+1,mid+1,sr,max(l,mid+1),r,s),
            s
        );
    }

    void update(vector<int> &seg,int si,int sl,int sr,int ind,char val,string &s){
        if(sl==sr){
            s[ind]=val;
            seg[si]=sl;
            return ;
        }

        int mid=(sl+sr)/2;
        if(ind<=mid)update(seg,si*2,sl,mid,ind,val,s);
        else update(seg,si*2+1,mid+1,sr,ind,val,s);
        seg[si]=Do(seg[si*2],seg[si*2+1],s);
    }

    int sum(int ind,vector<int> &BIT){
        if(ind<0)return 0;
        return BIT[ind]+sum((ind&(ind+1))-1,BIT);
    }

    void updateBIT(int ind,vector<int> &BIT,int val){
        if(ind>=BIT.size())return;
        BIT[ind]+=val;
        updateBIT(ind|(ind+1),BIT,val);
    }

    int DO(int k,vector<int> &BIT){
        int l=0,r=BIT.size()-1;
        int ans;
        while(l<=r){
            int mid=(l+r)/2;
            if(sum(mid,BIT)<=k)ans=mid,l=mid+1;
            else r=mid-1;
        }
        return ans;
    }


    string minInteger(string num, int k) {
        int n=num.size();
        vector<int> seg(4*n);
        build(seg,1,0,n-1,num);
        vector<int> BIT(n);
        for(int i=0;i<n;i++)updateBIT(i,BIT,1);

        string ans;
        vector<int> vis(n,0);
        for(int i=0;i<n;i++){
            int ind=DO(k+1,BIT);
            int aind=Q(seg,1,0,n-1,0,ind,num);
            ans+=num[aind];
            update(seg,1,0,n-1,aind,('9'+1),num);
            int sumi=sum(aind,BIT);
            k-=sumi;
            k++;
            updateBIT(aind,BIT,-1);

        }
        return ans;

    }
};
