class SegTree {
public:
    vector<int> nodes;
    int n;

    //constructor
    SegTree(int n) {
        this->n = n;
        nodes = vector<int>(this->n << 2);
    }

    void update(int ss, int se, int ql, int idx, int val) {
        //update[ql,ql]
        if (ql<ss or ql>se) {
            return;
        }
        if (ss == se) {
            nodes[idx] += val;
            return;
        }
        int mid = (ss + se) >> 1;
        update(ss, mid, ql, 2 * idx + 1, val);
        update(mid + 1, se, ql, 2 * idx + 2, val);
        nodes[idx] = nodes[2 * idx + 1] + nodes[2 * idx + 2];
    }

    int query(int ss, int se, int ql, int qr, int idx) {
        //ql aur qr ke beech me answer nikal
        if (ql > se or qr < ss) {
            return 0;
        }
        if (ql <= ss and se <= qr) {
            return nodes[idx];
        }
        int mid = (ss + se) >> 1;
        int lt = query(ss, mid, ql, qr, 2 * idx + 1);
        int rt = query(mid + 1, se, ql, qr, 2 * idx + 2);
        return lt + rt;
    }
    int querylessthan(int pos) {
        return query(0, n - 1, 0, pos - 1, 0); //query left is 0 and query right is pos-1
    }
    void increase(int pos) {
        update(0, n - 1, pos, 0, 1);
    }
};
class Solution {
public:
    string minInteger(string num, int k) {
        string ans = "";
        int n = num.size();
        SegTree* tree = new SegTree(n);
        vector<queue<int>> qs(10);
        for (int i = 0; i < n; i++) {
            qs[num[i] - '0'].push(i); //pushing back the positions for each digit from 0 to 9
        }
        for (int i = 0; i < n; i++) {
            for (int d = 0; d <= 9; d++) {
                if (qs[d].empty() == false) {
                    int pos = qs[d].front();


                    /*Explanation
                     Since few numbers already shifted to left, this `pos` might be outdated,so we try to find how many numbers already got shifted that were to the left of pos.
                    int shifted = seg.getCountLessThan(pos);
                    (pos - shift) is number of steps to make digit d move from pos to i.

                    */
					/*how many digits infront of this position "pos" has been moved to the right position*/
                    if (pos - shifted <= k) {
                        k -= (pos - shifted);
                        tree->increase(pos);
                        qs[d].pop();
                        ans += d + '0';
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
