            for (int dig = 0; dig < 10; dig++) {
                if (pos[dig].empty()) continue;
                int p = pos[dig].back();
                int actual = p + st.query(p);
                if (actual <= i + k) {
                    pos[dig].pop_back();
                    ans += to_string(dig);
                    st.update(p);
                    k -= actual - i;
                    break;
                }
            }
            i++;
        }
        vector<pair<int, int>> v;
        for (int dig = 0; dig < 10; dig++) {
            while (pos[dig].size()) {
                v.push_back({pos[dig].back(), dig});
                pos[dig].pop_back();
            }
        }
        sort(v.begin(), v.end());
        for (auto &[p, d] : v)
            ans += to_string(d);

        return ans;
    }
};
