class Solution {
    public String minInteger(String num, int k) {
        StringBuilder ans=new StringBuilder();
        int n=num.length();
        List<Queue<Integer>> pqs = new ArrayList<>();
        for (int i = 0; i <= 9; ++i) {
            pqs.add(new LinkedList<>());
        }
        for (int i = 0; i < num.length(); ++i) {
            pqs.get(num.charAt(i) - '0').add(i);
        }
        BIT bit=new BIT(n);
        for(int i=0; i<n; i++){
            // System.out.println(i);
            for (int d = 0; d <= 9; ++d) {
                // System.out.println("  "+d);
                if(pqs.get(d).size()>0){
                    int pos=pqs.get(d).peek();
                    int shift=bit.query(pos+1);
                    // System.out.println("   "+pos+" "+shift);
                    if(pos-shift<=k){
                        k-=pos-shift;
                        bit.update(pos+1);
                        ans.append(d);
                        // System.out.println("    "+k+" "+ans);
                        pqs.get(d).remove();
                        break;
                    }
                }
            }
        }
        return ans.toString();
    }
