class Solution {
    public String minInteger(String num, int k) {
        ArrayList<Character> arr = new ArrayList<Character>();
        for(int i=0;i<num.length();i++){
            arr.add(num.charAt(i));
        }
        minimizer(0,arr , k);
        String ans = "";
        for(char i:arr){
            ans = ans + i;
        }
        return ans;
    }
    public void minimizer(int pos , ArrayList<Character> arr , int k){
        if(k==0 || pos >= arr.size()){
            return;
        }
        int n = arr.size();
        int swaps = Math.min( k , n - 1 - pos);
        char min = '9';
        int min_pos = pos;
        for(int i=pos;i<pos + swaps + 1;i++){
            if(arr.get(i) < min ){
                min = arr.get(i);
                min_pos = i;
            }
        }
        arr.remove(min_pos);
        arr.add(pos , min);
        minimizer(pos+1 , arr  , k - (min_pos - pos));

    }
}

